mapper.select:
SELECT OP.ORDER_PRINT_CODE , OP.REASON , DATE_FORMAT(OP.PRINTED_AT, '%Y-%m-%d') AS printed_at , M.MEMBER_CODE , M.NAME , M.SIGNATURE_URL , OP.LETTER_OF_PURCHASE_CODE FROM tbl_order_print OP INNER JOIN tbl_member M ON OP.MEMBER_CODE = M.MEMBER_CODE WHERE OP.ACTIVE = TRUE
AND PRINTED_AT BETWEEN #{startDate} AND #{endDate}
ORDER BY M.NAME ASC
ORDER BY M.NAME DESC
ORDER BY OP.ORDER_PRINT_CODE DESC
LIMIT #{ pageSize } OFFSET #{ offset }

SELECT COUNT(*)
FROM tbl_order_print
WHERE ACTIVE = TRUE AND PRINTED_AT BETWEEN #{startDate} AND #{endDate}

WITH LatestOrderApprover AS ( SELECT MEMBER_CODE , ORDER_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY ORDER_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_order_approver WHERE ACTIVE = TRUE ), LatestExchangeApprover AS ( SELECT MEMBER_CODE , EXCHANGE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY EXCHANGE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_exchange_approver WHERE ACTIVE = TRUE ), LatestReturnApprover AS ( SELECT MEMBER_CODE , RETURN_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY RETURN_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_return_approver WHERE ACTIVE = TRUE ), LatestPurchaseApprover AS ( SELECT MEMBER_CODE , LETTER_OF_PURCHASE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY LETTER_OF_PURCHASE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_letter_of_purchase_approver WHERE ACTIVE = TRUE ) SELECT 'ORDER' AS KIND , CONCAT(O.CREATED_AT, ' ', O.ORDER_CODE, '번 주문 기안자 코드 : ', O.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , O.CREATED_AT AS DATE , CAST(O.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , O.ORDER_CODE AS CODE FROM tbl_order O INNER JOIN tbl_member DM ON O.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestOrderApprover OA ON O.ORDER_CODE = OA.ORDER_CODE INNER JOIN tbl_member AM ON OA.MEMBER_CODE = AM.MEMBER_CODE WHERE OA.RN = 1 AND O.ACTIVE = TRUE AND OA.ACTIVE = TRUE AND O.MEMBER_CODE = #{ memberCode }
AND O.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND O.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'EXCHANGE' AS KIND , CONCAT(E.CREATED_AT, ' ', E.ORDER_CODE, '번 교환 기안자 코드 : ', E.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , E.CREATED_AT AS DATE , CAST(E.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , E.EXCHANGE_CODE AS CODE FROM tbl_exchange E INNER JOIN tbl_member DM ON E.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestExchangeApprover EA ON E.EXCHANGE_CODE = EA.EXCHANGE_CODE INNER JOIN tbl_member AM ON EA.MEMBER_CODE = AM.MEMBER_CODE WHERE EA.RN = 1 AND E.ACTIVE = TRUE AND EA.ACTIVE = TRUE AND E.MEMBER_CODE = #{ memberCode }
AND E.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND E.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'RETURN' AS KIND , CONCAT(R.CREATED_AT, ' ', R.ORDER_CODE, '번 반품 기안자 코드 : ', R.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , R.CREATED_AT AS DATE , CAST(R.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , R.RETURN_CODE AS CODE FROM tbl_return R INNER JOIN tbl_member DM ON R.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestReturnApprover RA ON R.RETURN_CODE = RA.RETURN_CODE INNER JOIN tbl_member AM ON RA.MEMBER_CODE = AM.MEMBER_CODE WHERE RA.RN = 1 AND R.ACTIVE = TRUE AND RA.ACTIVE = TRUE AND R.MEMBER_CODE = #{ memberCode }
AND R.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND R.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'PURCHASE' AS KIND , CONCAT(P.CREATED_AT, ' ', P.LETTER_OF_PURCHASE_CODE, '번 발주 기안자 코드 : ', P.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , P.CREATED_AT AS DATE , CAST(P.APPROVED AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , P.LETTER_OF_PURCHASE_CODE AS CODE FROM tbl_letter_of_purchase P INNER JOIN tbl_member DM ON P.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestPurchaseApprover PA ON P.LETTER_OF_PURCHASE_CODE = PA.LETTER_OF_PURCHASE_CODE INNER JOIN tbl_member AM ON PA.MEMBER_CODE = AM.MEMBER_CODE WHERE PA.RN = 1 AND P.ACTIVE = TRUE AND PA.ACTIVE = TRUE AND P.MEMBER_CODE = #{ memberCode }
AND P.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND P.APPROVED = #{ approval }
ORDER BY DATE ASC
ORDER BY DATE DESC
ORDER BY DATE DESC
LIMIT #{ pageSize } OFFSET #{ offset }

SELECT
SUM(COUNT)
FROM (
SELECT
COUNT(*) AS COUNT
FROM tbl_order O
WHERE O.ACTIVE = TRUE AND O.MEMBER_CODE = #{ memberCode }

  AND O.CREATED_AT BETWEEN #{startDate} AND #{endDate}


  AND O.APPROVAL_STATUS = #{ approval }

UNION ALL
SELECT
COUNT(*) AS COUNT
FROM tbl_exchange E
WHERE E.ACTIVE = TRUE AND E.MEMBER_CODE = #{ memberCode }

  AND E.CREATED_AT BETWEEN #{startDate} AND #{endDate}


  AND E.APPROVAL_STATUS = #{ approval }

UNION ALL
SELECT
COUNT(*) AS COUNT
FROM tbl_return R
WHERE R.ACTIVE = TRUE AND R.MEMBER_CODE = #{ memberCode }

  AND R.CREATED_AT BETWEEN #{startDate} AND #{endDate}


  AND R.APPROVAL_STATUS = #{ approval }

UNION ALL
SELECT
COUNT(*) AS COUNT
FROM tbl_letter_of_purchase P
WHERE  P.ACTIVE = TRUE AND P.MEMBER_CODE = #{ memberCode }

  AND P.CREATED_AT BETWEEN #{startDate} AND #{endDate}


  AND P.APPROVED = #{ approval }

) AS UNION_RESULT_SET

WITH LatestOrderApprover AS ( SELECT MEMBER_CODE , ORDER_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY ORDER_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_order_approver WHERE ACTIVE = TRUE ), LatestExchangeApprover AS ( SELECT MEMBER_CODE , EXCHANGE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY EXCHANGE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_exchange_approver WHERE ACTIVE = TRUE ), LatestReturnApprover AS ( SELECT MEMBER_CODE , RETURN_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY RETURN_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_return_approver WHERE ACTIVE = TRUE ), LatestPurchaseApprover AS ( SELECT MEMBER_CODE , LETTER_OF_PURCHASE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY LETTER_OF_PURCHASE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_letter_of_purchase_approver WHERE ACTIVE = TRUE ) SELECT 'ORDER' AS KIND , CONCAT(O.CREATED_AT, ' ', O.ORDER_CODE, '번 주문 기안자 코드 : ', O.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , O.CREATED_AT AS DATE , CAST(O.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , O.ORDER_CODE AS CODE FROM tbl_order O INNER JOIN tbl_member DM ON O.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestOrderApprover OA ON O.ORDER_CODE = OA.ORDER_CODE INNER JOIN tbl_member AM ON OA.MEMBER_CODE = AM.MEMBER_CODE WHERE OA.RN = 1 AND O.ACTIVE = TRUE AND OA.ACTIVE = TRUE AND OA.MEMBER_CODE = #{ memberCode }
AND O.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND O.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'EXCHANGE' AS KIND , CONCAT(E.CREATED_AT, ' ', E.ORDER_CODE, '번 교환 기안자 코드 : ', E.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , E.CREATED_AT AS DATE , CAST(E.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , E.EXCHANGE_CODE AS CODE FROM tbl_exchange E INNER JOIN tbl_member DM ON E.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestExchangeApprover EA ON E.EXCHANGE_CODE = EA.EXCHANGE_CODE INNER JOIN tbl_member AM ON EA.MEMBER_CODE = AM.MEMBER_CODE WHERE EA.RN = 1 AND E.ACTIVE = TRUE AND EA.ACTIVE = TRUE AND EA.MEMBER_CODE = #{ memberCode }
AND E.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND E.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'RETURN' AS KIND , CONCAT(R.CREATED_AT, ' ', R.ORDER_CODE, '번 반품 기안자 코드 : ', R.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , R.CREATED_AT AS DATE , CAST(R.APPROVAL_STATUS AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , R.RETURN_CODE AS CODE FROM tbl_return R INNER JOIN tbl_member DM ON R.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestReturnApprover RA ON R.RETURN_CODE = RA.RETURN_CODE INNER JOIN tbl_member AM ON RA.MEMBER_CODE = AM.MEMBER_CODE WHERE RA.RN = 1 AND R.ACTIVE = TRUE AND RA.ACTIVE = TRUE AND RA.MEMBER_CODE = #{ memberCode }
AND R.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND R.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT 'PURCHASE' AS KIND , CONCAT(P.CREATED_AT, ' ', P.LETTER_OF_PURCHASE_CODE, '번 발주 기안자 코드 : ', P.MEMBER_CODE) AS TITLE , DM.NAME AS DRAFTER_NAME , P.CREATED_AT AS DATE , CAST(P.APPROVED AS CHAR) AS STATUS , AM.NAME AS APPROVER_NAME , P.LETTER_OF_PURCHASE_CODE AS CODE FROM tbl_letter_of_purchase P INNER JOIN tbl_member DM ON P.MEMBER_CODE = DM.MEMBER_CODE INNER JOIN LatestPurchaseApprover PA ON P.LETTER_OF_PURCHASE_CODE = PA.LETTER_OF_PURCHASE_CODE INNER JOIN tbl_member AM ON PA.MEMBER_CODE = AM.MEMBER_CODE WHERE PA.RN = 1 AND P.ACTIVE = TRUE AND PA.ACTIVE = TRUE AND PA.MEMBER_CODE = #{ memberCode }
AND P.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND P.APPROVED = #{ approval }
ORDER BY DATE ASC
ORDER BY DATE DESC
ORDER BY DATE DESC
LIMIT #{ pageSize } OFFSET #{ offset }

SELECT SUM(COUNT) FROM ( WITH LatestOrderApprover AS ( SELECT MEMBER_CODE , ORDER_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY ORDER_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_order_approver WHERE ACTIVE = TRUE ), LatestExchangeApprover AS ( SELECT MEMBER_CODE , EXCHANGE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY EXCHANGE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_exchange_approver WHERE ACTIVE = TRUE ), LatestReturnApprover AS ( SELECT MEMBER_CODE , RETURN_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY RETURN_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_return_approver WHERE ACTIVE = TRUE ), LatestPurchaseApprover AS ( SELECT MEMBER_CODE , LETTER_OF_PURCHASE_CODE , ACTIVE , APPROVED , ROW_NUMBER() OVER (PARTITION BY LETTER_OF_PURCHASE_CODE ORDER BY APPROVED ASC) AS RN FROM tbl_letter_of_purchase_approver WHERE ACTIVE = TRUE ) SELECT COUNT(*) AS COUNT FROM tbl_order O INNER JOIN LatestOrderApprover OA ON OA.ORDER_CODE = O.ORDER_CODE WHERE O.ACTIVE = TRUE AND OA.MEMBER_CODE = #{ memberCode }
AND O.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND O.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT COUNT(*) AS COUNT FROM tbl_exchange E INNER JOIN LatestExchangeApprover EA ON EA.EXCHANGE_CODE = E.EXCHANGE_CODE WHERE E.ACTIVE = TRUE AND EA.MEMBER_CODE = #{ memberCode }
AND E.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND E.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT COUNT(*) AS COUNT FROM tbl_return R INNER JOIN LatestReturnApprover RA ON RA.RETURN_CODE = R.RETURN_CODE WHERE R.ACTIVE = TRUE AND RA.MEMBER_CODE = #{ memberCode }
AND R.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND R.APPROVAL_STATUS = #{ approval }
UNION ALL SELECT COUNT(*) AS COUNT FROM tbl_letter_of_purchase P INNER JOIN LatestPurchaseApprover PA ON PA.LETTER_OF_PURCHASE_CODE = P.LETTER_OF_PURCHASE_CODE WHERE P.ACTIVE = TRUE AND PA.MEMBER_CODE = #{ memberCode }
AND P.CREATED_AT BETWEEN #{startDate} AND #{endDate}
AND P.APPROVED = #{ approval }
) AS UNION_RESULT_SET
