mapper.select:
SELECT P.LETTER_OF_PURCHASE_CODE, P.CREATED_AT, M.NAME AS MEMBER_NAME, A.LETTER_OF_PURCHASE_CODE AS PURCHASE_CODE, A.MEMBER_CODE AS APPROVER_CODE, A.APPROVED, A.APPROVED_AT, A.COMMENT, B.NAME AS APPROVER_NAME, O.NAME AS POSITION_NAME FROM tbl_letter_of_purchase P JOIN tbl_member M ON P.MEMBER_CODE = M.MEMBER_CODE JOIN tbl_letter_of_purchase_approver A ON P.LETTER_OF_PURCHASE_CODE = A.LETTER_OF_PURCHASE_CODE JOIN tbl_member B ON A.MEMBER_CODE = B.MEMBER_CODE JOIN tbl_position O ON B.POSITION_CODE = O.POSITION_CODE
P.LETTER_OF_PURCHASE_CODE = #{letterOfPurchaseCode}
ORDER BY A.APPROVED_AT ASC
