mapper.select:
SELECT
       (SELECT
               COUNT(*)
          FROM tbl_order
         WHERE active = true

           AND created_at BETWEEN #{startDate} AND #{endDate}
        ) AS order_count,
      (SELECT
              COUNT(*)
        FROM tbl_exchange
       WHERE active = true

         AND created_at BETWEEN #{startDate} AND #{endDate}
      ) AS exchange_count,
      (SELECT
              COUNT(*)
         FROM tbl_return
        WHERE active = true

         AND created_at BETWEEN #{startDate} AND #{endDate}
      ) AS return_count;

SELECT OI.item_code , I.name as item_name , SUM(OI.quantity) as item_count FROM tbl_order O INNER JOIN tbl_order_item OI ON O.order_code = OI.order_code INNER JOIN tbl_item I ON OI.item_code = I.item_code WHERE O.active = true
AND O.created_at BETWEEN #{startDate} AND #{endDate}
GROUP BY OI.item_code, I.name ORDER BY SUM(OI.quantity) DESC LIMIT 10
